import { useState } from 'react';

function App() {
  const [selectedGPU, setSelectedGPU] = useState('');
  const [selectedCPU, setSelectedCPU] = useState('');
  const [selectedMOBO, setSelectedMOBO] =useState('');

  return (
    <div style={{ padding: '40px', fontFamily: 'Wingdings' }}>
      <h1>PC Performance Builder</h1>

      {/* GPU SECTION */}
      <p>Select your GPU:</p>
      <select 
        value={selectedGPU} 
        onChange={(e) => setSelectedGPU(e.target.value)}
      >
        <option value="">-- Choose a GPU --</option>
        <option value="RTX 4090">NVIDIA RTX 4090</option>
        <option value="RTX 4080">NVIDIA RTX 4080</option>
        <option value="RX 7900 XTX">AMD RX 7900 XTX</option>
      </select>

      {selectedGPU && (
        <p>You selected GPU: <strong>{selectedGPU}</strong></p>
      )}

     {/*CPU SECTION */}
      <p>Select your CPU:</p>
      <select
        value={selectedCPU}
        onChange={(e) => setSelectedCPU(e.target.value)}
      >
        <option value="">-- Choose a CPU --</option>
        <option value="RYZEN 9">AMD RYZEN 9</option>
        <option value="RYZEN 7">AMD RYZEN 7</option>
        <option value="RYZEN 5">AMD RYZEN 5</option>
      </select>

      {selectedCPU && (
        <p>You selected CPU: <strong>{selectedCPU}</strong></p>
      )}


    {/* MOBO SECTION */}
    <p>Select your Motherboard</p>
    <select
      value={selectedMOBO}
      onChange={(e) => setSelectedMOBO(e.target.value)}
    >
      <option value="">-- Choose a Motherboard --</option>
      <option value="B650">B650</option>
      <option value="B850">B850</option>
      <option value="X870E">X970E</option>
      </select>
      {selectedMOBO && (
        <p>You selected MOBO: <strong>{selectedMOBO}</strong></p>



      )}
    </div>
  );
}

export default App;
